package com.infinite.product;

public class SwitchExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 2;
		switch(2)
		{
		case 0:
			System.out.print("Arun");
			break;
		case 1:
			System.out.print("run");
			break;
		default:
			System.out.print("Arun Kumar");
			break;
		case 2:
			System.out.print("un");
			System.exit(1);
		case 3:
			System.out.print("n");
			break;
		
		}
	}

}
